/* ==:: COMMON STYLE ::== */

html { -webkit-font-smoothing:antialiased; -webkit-text-size-adjust: 100%; -ms-text-size-adjust: 100% }
body { -webkit-font-smoothing: subpixel-antialiased; -webkit-text-stroke-width: 0; background: $theme-white-color;
  -moz-osx-font-smoothing: auto; font-family: $theme-base-font-family; color: $theme-body-font-color; padding: 0;}
.body{
  margin-bottom: 50px; min-height: 400px;
  @include breakpoint(medium down) {
    margin-bottom: 30px;
  }
}
.type-default{
  .body{
    margin-bottom: 0px;
  }
}
input[type="text"], input[type="password"], input[type="date"], input[type="datetime"], input[type="datetime-local"],
input[type="month"], input[type="week"], input[type="email"], input[type="number"], input[type="search"], input[type="tel"],
input[type="time"], input[type="url"], input[type="color"], textarea{ font-size: 1rem; outline: none; }

textarea{ resize: vertical;}

h1, .h1, h2, .h2, h3, .h3, h4, .h4, h5, .h5, h6, .h6{
  margin: 0 0 20px 0; padding: 0; font-weight: normal; font-family: $theme-base-title-font-family; font-weight: $font-weight-500;
  @include breakpoint(mob-large down) {
      margin: 0 0 10px 0;
  }
}
h1, .h1{ font-size: 22px; }
h2, .h2{ font-size: 20px; }
h3, .h3{ font-size: 18px; }
h4, .h4{ font-size: 17px; }
h5, .h5{ font-size: 16px; }
h6, .h6{ font-size: 15px; }

p{ margin-bottom: 20px; line-height: 24px;}
a{ color: $theme-body-link-color;   outline: none; text-decoration: none;
   &:hover, &:focus{
     color: $theme-primary-color;
   }
}
i{color: $theme-body-link-color;
  &:hover{
    color: $theme-primary-color;
  }
}
.icon-star1, .icon-star{
  font-size: 12px; color: $theme-orange-color;
}
ul, ol{
  margin-left: 20px; margin-bottom: 20px;
  @include breakpoint(small down) {
    margin-bottom: 10px;  margin-left: 10px;
  }
}
img{
  max-width: 100%; vertical-align: middle;
}
select option{
  padding: 4px 8px; font-size: 13px; border: 1px solid $theme-black-color !important;
}
.button.button--primary{
  background: $theme-primary-color; color: $theme-white-color; border-color: $theme-primary-color;
  &:hover, &:focus, &:active{
    background: $theme-white-color; color: $theme-primary-color; border-color: $theme-primary-color;
  }
}

.button.demo{
    padding: 15px; min-width: 134px; text-transform: uppercase; font-weight: 400; font-size: 12px; letter-spacing: 1px;   border: none; color: $theme-black-color; line-height: normal; background: $theme-white-color;
    &:hover{
      background: $theme-black-color; color: $theme-white-color;
    }
}

.button{
  font-size: 13px; border: none; @include border-radius(0); line-height: 18px; padding: 10px 15px; border: 1px solid $theme-primary-color; background: $theme-white-color;font-family: $theme-base-font-family; color: $theme-primary-color; text-transform: uppercase;

  &:hover, &:focus, &:active{
    background: $theme-primary-color; color: $theme-white-color; border: 1px solid $theme-primary-color;
  }
  &.btn-black-border{
    border: 1px solid $theme-black-color; color: $theme-black-color;
    &:hover, &:focus, &:active{
      color: $theme-white-color; border: 1px solid $theme-white-color;
    }
  }
  &.btn-small{
    padding: 8px 13px; line-height: 16px; font-size: 12px;
  }
  &.btn-large{
    padding: 14px 30px; line-height: 14px; font-size: 16px; font-weight: 300; background: $theme-primary-color;  color: $theme-white-color;
    &:hover{
      background: $theme-black-color; border: 1px solid $theme-black-color;
    }
  }
  &.btn-big{
    padding: 16px 21px; line-height: 18px; font-size: 20px;   color: $theme-black-color; background: transparent; border-color: $theme-black-color;
    &:hover{
      background: $theme-primary-color; color: $theme-white-color; border: 1px solid $theme-primary-color;
    }
  }
  &.btn-theme-color{
    background: $theme-primary-color; color: $theme-white-color; border-color: $theme-primary-color;
    &:hover, &:focus, &:active{
      background: $theme-white-color; color: $theme-primary-color; border-color: $theme-primary-color;
    }
  }
}
.theme-primary-color{
  color: $theme-primary-color;
}
.milano-layout--Blue{
