.slick-dots{
  li{
    margin: 0 3px; display: inline-block; vertical-align: bottom; height: auto; padding: 0px; width: 20px;
    button{
      height: 5px; width: 20px; border: 1px solid $theme-primary-color; vertical-align: bottom;
      -webkit-transition: all 0.2s ease; transition: all 0.2s ease; border-radius: 0px;
      &:before{
        display: none;
      }
      &:hover{
        background: $theme-primary-color;
      }
    }
    &.slick-active{
      button{
        height: 10px; background: $theme-primary-color;
      }
    }
  }
