// CAROSUAL ARROW CSS STYLE ----------------------------------------------------

/* ==:: CAROSUAL ARROW CSS STYLE START ::== */

.milano-layout--Blue{
    .slick-prev, .slick-next{
      top: -12px;
      &:before{
        font-size: 23px;
      }
    }
}

  .slick-prev, .slick-next{
    display: inline-block; right: 11px; left: auto; top: -12px; height: 30px; width: 30px; background: none; border: none;
    opacity: 1; margin: 0px; padding: 0px; line-height: 30px;
    &:before{
      height: 30px;  width: 30px; background: none; position: absolute;  font-family: 'icomoon'; left: 0; font-size: 26px;      line-height: 30px; color: $theme-body-dark-font-color;
    }
    &:hover{
      &:before{
      background-image: none; color: $theme-primary-color;
      }
    }
  }
  .slick-prev{
    right: 46px;
    &:before{
      content: "\e97c";
    }
  }
  .slick-next{
    &:before{
      content: "\e97d";
    }
  }
  .slick-disabled{
    &:before{
      cursor: not-allowed;
    }
    &:hover{
      &:before{
        color: $theme-body-font-color;
      }
    }
  }

/* ==:: CAROSUAL ARROW CSS STYLE START ::== */
// ANIMATION CSS STYLE ---------------------------------------------------------

/* ==:: ANIMATION CSS STYLE START ::== */

.banner-hover{
      overflow: hidden;
  a{
    display: block;
    img{
      width: 100%; -webkit-transition: all 0.5s ease-in-out; -moz-transition: all 0.5s ease-in-out;
      transition: all 0.5s ease-in-out;
    }
  }
  span{
    position: relative;
    display: inline-block;
    &:before{
      width: 100%; height: 100%;  content: "";  position: absolute;  left: 0;  right: 0;  bottom: 0; top: 0; overflow: hidden;
      background: rgba(0,0,0,0.4);  opacity: 0; z-index: 9;
    }
  }
  &:hover{
      span{
        &:before{
          opacity: 1;
        }
      }
    a{
      img{
        opacity: 0.7;
        -moz-transform: scale3d(1.05, 1.05, 1.05);
        -webkit-transform: scale3d(1.05, 1.05, 1.05);
        transform: scale3d(1.05, 1.05, 1.05);
        -webkit-transition: all 0.5s ease-in-out;
        -moz-transition: all 0.5s ease-in-out;
        transition: all 0.5s ease-in-out;
      }
    }
  }
}
/* ==:: ANIMATION CSS STYLE END ::== */

