.r-tabs .r-tabs-nav {
    margin: 0;
    padding: 0;
}
.r-tabs .r-tabs-tab {
    display: inline-block;
    margin: 0;
    list-style: none;
}
.r-tabs .r-tabs-panel {
    padding: 15px;
    display: none;
    @include breakpoint(medium down){
      padding-bottom: 5px;
      p{
        margin-bottom: 10px;
      }
    }
}
.r-tabs .r-tabs-accordion-title {
    display: none;
}
.r-tabs .r-tabs-panel.r-tabs-state-active {
    display: block;
}
/* Accordion responsive breakpoint */
@media only screen and (max-width: 768px) {
  .r-tabs .r-tabs-nav {
      display: none;
  }
  .r-tabs .r-tabs-accordion-title {
      display: block;
      padding: 10px 5px;
      font-size: 16px;
      border-bottom: 1px solid $theme-black-color;
      a{
        display: block;
        position: relative;
        color: $theme-body-font-color;
        &:after{
          content: "\e978";
          font-family: 'icomoon';
          font-size: 14px;
          position: absolute;
          right: 15px;
          top: 5px;
        }
      }
  }
  .r-tabs .r-tabs-accordion-title.r-tabs-state-active{
    border-bottom: 1px solid $theme-orange-color;
      a{
        color: $theme-orange-color;
        &:after{
          content: "\e97f";
          font-family: 'icomoon';
          font-size: 14px;
          position: absolute;
          right: 15px;
          top: 5px;
        }
      }
  }
}

