// CookieMessage css -------------------------------------------------------
/* ==:: CookieMessage START ::== */

.cookieMessage{
  position: fixed;
  bottom: 0;
  z-index: 100;
  width: 100%;
  text-align: center;
  background: rgba($theme-black-color, 0.5);
  .cookieMessage-container{
    width: 100%;
    @include breakpoint(medium down){
      padding: 15px 10px;
    }
  }
  .cookieMessage-text{
    width: auto;
    display: inline-block;vertical-align: middle;
    margin: 0px;
    color: $theme-white-color;
    p{
      margin: 0px;
      @include breakpoint(medium down){
        margin-bottom: 10px;
        line-height: 22px;
      }
    }
  }
  .button.button--primary{
    width: auto;
    display: inline-block;
    vertical-align: middle;
    margin: 0px;
    @include breakpoint(medium down){
      padding: 8px 18px;
    }
  }
}
/* ==:: CookieMessage END ::== */

// HomePage Carousel css -------------------------------------------------------
/* ==:: Home Page Carousel START ::== */
.heroCarousel-down-arrow{
  width: 70px;
  height: 35px;
  border-top-left-radius: 70px;
  border-top-right-radius: 70px;
  background: $theme-orange-color;
  position: fixed;
  z-index: 1;
  text-align: center;
  line-height: 53px;
  left: 50%;
  bottom: 0;
  transform: translate(-50%);
  -ms-transform: translate(-50%);
  -webkit-transform: translate(-50%);
  -moz-transform: translate(-50%);
  -o-transform: translate(-50%);
  @include breakpoint(small down){
    width: 50px;
    height: 25px;
    border-top-left-radius: 50px;
    border-top-right-radius: 50px;
    line-height: 40px;
  }
  i{
    font-size: 20px;
    color: $theme-white-color;
    font-weight: bold;
    @include breakpoint(small down){
      font-size: 16px;
    }
  }
  &:hover{
    background: $theme-white-color;
    i{
      color: $theme-orange-color;
    }
  }
}
.heroCarousel{
  margin-top: 0px;
  @include breakpoint(medium down) {
    margin-bottom: 0px !important;
  }
  @include breakpoint(mob-large down){
    .slick-dots{
          bottom: 0;
          top: inherit;
      li{
        margin: 0 3px; display: inline-block; vertical-align: bottom; height: auto; padding: 0px; width: 20px;
        button{
          height: 5px; width: 20px; border: 1px solid $theme-primary-color; vertical-align: bottom;
          -webkit-transition: all 0.2s ease; transition: all 0.2s ease; border-radius: 0px;
          &:before{
            display: none;
          }
          &:hover{
            background: $theme-primary-color;
          }
        }
        &.slick-active{
          button{
            height: 10px; background: $theme-primary-color;
          }
        }
      }
    }
  }
  .slick-next{
    display: none !important;
  }
 .slick-prev{
   display: none !important;
 }
 .slick-dots{
   top: 50%;
   right: 50px;
   bottom: inherit;
   width: auto;
   text-align: left;
   transform: translate(-50%, -50%);
   -webkit-transform: translate(-50%, -50%);
   -moz-transform: translate(-50%, -50%);
     @include breakpoint(mob-large down){
       bottom: 10px;
       top: inherit;
       width: 100%;
       right: auto;
       left: auto;
       text-align: center;
       transform:none;
     }
     @supports (-ms-ime-align:auto) {
       transform: translate(-50%, -50%);
       -webkit-transform: translate(-50%, -50%);
       -moz-transform: translate(-50%, -50%);
     }
   li{
     background: transparent;
     padding: 0px;
     display: block;
     text-align: right;
     position: relative;
     width: 2.28571rem;
     height: 2.28571rem;
     @include breakpoint(mob-large down){
        margin: 0 3px; display: inline-block; vertical-align: bottom; height: auto; padding: 0px; width: 20px;
        &:after{
          display: none;
        }
     }
     &:after{
      width: 25px;
      height: 2px;
      background-color: $theme-black-color;
      position: absolute;
      content: "";
      bottom: 0;
      right: 0;
      left: auto;
      transition: all 0.2s ease;
      -webkit-transition: all 0.2s ease;
      -moz-transition: all 0.2s ease;
         @supports (-ms-ime-align:auto) {
           transition: all 0.2s ease;
           -webkit-transition: all 0.2s ease;
           -moz-transition: all 0.2s ease;
         }
     }
     &:before{
       background: transparent;
     }
     button{
       background: transparent;
       border-radius: 0px;
       border: none;
       font-size: 14px;
       color: $theme-black-color;
       text-align: right;
        vertical-align: middle;
        height: 100%;
          @include breakpoint(mob-large down){
            font-size: 0;
            height: 5px;
            width: 20px;
            border: 1px solid $theme-primary-color;
            vertical-align: bottom;
            transition: all 0.2s ease;
            border-radius: 0px;
          }
       &:hover, &:focus{
         color: $theme-orange-color;
         background: none;
       }
       &:before{
         display: none;
       }
     }
     &:hover{
       &:after{
         background-color: $theme-primary-color;
       }
     }
   }
   li.slick-active{
      &:after{
      width: 45px;
      height: 2px;
      background-color: $theme-orange-color;
      position: absolute;
      content: "";
      left: -12px;
      right: auto;
     }
     button{
       height: 100%;
       color: $theme-orange-color;
       @include breakpoint(mob-large down){
         height: 10px;
         background: $theme-orange-color;
       }
       &:before{
          background: transparent;
       }
     }
   }
 }
}
.product-section{
  .slick-dotted.slick-slider{
      margin-bottom: 30px;
    @include breakpoint(medium down) {
      margin-bottom: 0px;
    }
  }
  .slick-list{
    padding-bottom: 5px;
  }
}
.heroCarousel-wrap{
  .heroCarousel{
    .heroCarousel-content{
      top: 30%;
    }
  }
  .slick-initialized {
    .heroCarousel-content{
      display:block;
      top: 48%;
    }
  }
}
.slick-active{
  .animated {
    -webkit-animation-duration : 2s  ;
    animation-duration : 2s  ;

    -webkit-animation-delay : 0.1s  ;
    animation-delay : 0.1s  ;
  }

  @keyframes slideInUp {
    from {
      transform: translate3d(0, 100%, 0);
      visibility: visible;
    }
    to {
      transform: translate3d(0, 0, 0);
    }
  }
  .slideInUp {
    animation-name: slideInUp;
  }
  @keyframes slideInDown {
    from {
      transform: translate3d(0, -100%, 0);
      visibility: visible;
    }
    to {
      transform: translate3d(0, 0, 0);
    }
  }
  .slideInDown {
    animation-name: slideInDown;
  }
  @keyframes fadeIn {
    from {
      opacity: 0;
    }
    to {
      opacity: 1;
    }
  }
  .fadeIn {
    animation-name: fadeIn;
  }
}
/* ==:: Home Page Carousel END ::== */

