       @include breakpoint(small down) {
         margin-left: -18px;
         margin-right: -18px;
       }
  li{
     margin-bottom: 20px; width: 14.7%; border: 1px solid $theme-border-color; padding: 5px; margin-left: 20px;
     @include breakpoint(xlarge down) {
       width: 17.6%;
     }
     @include breakpoint(large down) {
       width: 21.7%;
     }
     @include breakpoint(medium down) {
       width: 28.2%;
     }
     @include breakpoint(mob-large down) {
        width: 29.3%;
     }
     @include breakpoint(mob-mid down) {
        width: 28.7%;
     }
     @include breakpoint(mob-small down) {
        width: 27.7%;
     }
     @include breakpoint(small down) {
       width: 41%;
     }
    a{
      display: block;   color: $theme-body-grey-color; margin: 0 auto;
      &:hover{
        color: $theme-primary-color;
      }
    }
    .sub-category-img{
       display: table-cell;
       overflow: hidden;
       width: 160px;
       max-width: 100%;
       vertical-align: middle;
       @include breakpoint(medium down){
         width: 140px;
       }
       @include breakpoint(mob-large down){
         width: 180px;
       }
       @include breakpoint(mob-mid down){
         width: 148px;
       }
      img{
        display: inline-block;
        max-width: 160px;
        @include breakpoint(medium down){
          max-width: 140px;
        }
        @include breakpoint(mob-large down){
          max-width: 180px;
        }
        @include breakpoint(mob-mid down){
          max-width: 148px;
        }
        @include breakpoint(mob-small down){
          max-width: 120px;
        }
      }
    }
    .category-image-inner{
      position: relative;
      width: 100%;
      &:hover{
        a{
          color: $theme-primary-color;
        }
      }
    }
    .sub-category-name{
        margin-bottom: 10px; font-size: 13px; line-height: normal;
        display: inline-block;margin-top: 10px;
        a{
          color: $theme-black-color;
          &:hover{
            color: $theme-primary-color;
          }
        }
    }

    &.column:last-child:not(:first-child), .columns:last-child:not(:first-child){
      float: left;
    }
    &:hover{
      .sub-category-img-block{
        img{
          opacity: 0.7;
        }
      }
    }
  }
}
/* ==:: CATEGORY PAGE STYLE END ::==*/
// PRODUCT DETAIL STYLE ---------------------------------------------------------
/* ==:: PRODUCT DETAIL PAGE STYLE  START ::==*/
.type-product.milano-layout--Blue {
   .card{
     margin-bottom: 50px;
     @include breakpoint(medium down){
       margin-bottom: 0px;
     }
   }
   .usp-section{
     + .related-product{
       margin-top: 20px;
       @include breakpoint(medium down){
         margin-top: 0px;
       }
     }
   }
   .related-product{
     .card{
       .card-body{
         .card-btn{
            display: none;
            @include breakpoint(medium down){
              display: block;
            }
         }
       }
     }
   }
}
.icon--ratingFull{
  svg{
    fill: $theme-primary-color;
  }
}
.icon--ratingEmpty{
  svg{
    fill: lightern($theme-dark-grey-color, 20%);
  }
}
.call-for-price{
  font-weight: 300;
  strong{
    display: inline-block; vertical-align: middle; font-weight: $font-weight-500; font-size: 18px; line-height: 20px;
  }
  .icon-call-hold{
    margin-right: 10px; color: $theme-primary-color; display: none;
  }
  span{
    display: inline-block; vertical-align: bottom; font-size: 16px;
  }
}
.wishlist-popup{
  .wishlist_dropdownbox{
    text-align: center;
    .wishlistid{
      outline: none !important;
      display: inline-block;
      vertical-align: middle;
      border: 1px solid $theme-black-color;
      padding: 2px 5px;
      background: none;
      text-transform: capitalize;
      margin-top: 10px;
      &:hover{
        border: 1px solid $stencil-primary-color;
        color: $stencil-primary-color;
      }
      + .wishlistid{
        margin-left: 10px;
      }
    }
    input[type="radio"]{
      border: 1px solid #000 !important;
      background: #f2f;
    }
    label{
      display: inline-block;
      vertical-align: middle;
      text-transform: capitalize;
       + .wishlistid {
         margin-left: 10px;
       }
    }
    .button{
      margin-bottom: 0px;
      display: inline-block;
      vertical-align: middle;
      margin: 0 auto;
      margin-top: 20px;
       + .button{
         margin-left: 16px;
       }
    }
  }
}
.product-tab-section{
  .productCarousel{
    margin-bottom: 0;
  }
  .tab-content, .tab-content.has-jsContent.is-active{
    padding-bottom: 0;
  }
  .slick-prev, .slick-next{
    font-size: 14px;
  }
  .slick-dotted{
    @include breakpoint(medium down){
      margin-bottom: 30px;
    }
  }
}
/* ==:: PRODUCT DETAIL PAGE STYLE END ::==*/
