// BRAND PAGE STYLE ------------------------------------------------------------
/* ==:: BRAND PAGE STYLE START ::==*/
.brandGrid {
      @include flex;
  -webkit-flex-wrap: wrap;
      -ms-flex-wrap: wrap;
          flex-wrap: wrap;
          &:before{
            display: none !important;
          }
          &:after{
            display: none !important;
          }
    .brand {
        margin-bottom: 20px;
        -webkit-flex-basis: 16.66%;
        -ms-flex-preferred-size: 16.66%;
        flex-basis: 16.66%;
        @include breakpoint(large down){
          -webkit-flex-basis: 25%;
          -ms-flex-preferred-size: 25%;
          flex-basis: 25%;
        }
        @include breakpoint(mob-mid down){
          -webkit-flex-basis: 33.33%;
          -ms-flex-preferred-size: 33.33%;
          flex-basis: 33.33%;
        }
        @include breakpoint(small down){
          -webkit-flex-basis: 50%;
          -ms-flex-preferred-size: 50%;
          flex-basis: 50%;
        }
    }
    .card {
        height: 100%;
        @include flex;
        -webkit-flex-wrap: wrap;
        -ms-flex-wrap: wrap;
        flex-wrap: wrap;
        -webkit-box-pack: center;
        -webkit-justify-content: center;
        -ms-flex-pack: center;
        justify-content: center;
        padding: 5px;
    }
    .card-figure-custom {
        margin-bottom: 0;
        margin-top: 0;
        align-self: center;
        width: 100%;
    }
    .card-body-custom {
        align-self: flex-end;
        @include flex;
        align-items: center;
        justify-content: center;
        margin-top: 10px;
        padding: 0 5px;
        width: 100%;
        .card-title{
          font-size: 14px;
          line-height: 18px;
          word-break: break-all;
        }
    }
}
.milano-layout--Blue{
  .brandGrid .brand .card{
    margin-top: 0px;
    .card-body{
     padding: 10px;
     height: inherit;
     .card-title{
       margin: 0px;
     }
   }
  }
}
.side-bar-img{
  border: 1px solid $theme-border-color;
}
/* ==:: BRAND PAGE STYLE END ::==*/

// CART PAGE STYLE -------------------------------------------------------------
/* ==:: CART PAGE STYLE START ::==*/
.cart-header{
  .cart-header-item{
    color: $theme-body-font-color;
    font-weight: 600;
    padding: 15px 10px;
  }
}
.cart-list{
  .cart-item-title{
    .cart-item-options--giftWrapping{
      margin-bottom: 0px;
      .cart-item-option-description{
        margin-bottom: 0px;
      }
    }
  }
  .cart-item-quantity{
    .form-increment{
      .form-input--incrementTotal{
        height: 50px;
        width: 50px !important;
        font-size: 14px;
        text-align: center;
        color: $theme-light-black-color;
        border: 1px solid $theme-dark-border-color;
        border-radius: 0px;
        float: left;
        font-weight: 300;
        padding: 5px;
        @include breakpoint(small down) {
          height: 40px;
          width: 40px !important;
          padding: 5px;
        }
      }
      .button{
        display: block;
        border: none;
        padding: 0px;
        background: transparent;
        i{
          font-size: 10px;
          line-height: 25px;
          text-align: center;
          width: 20px;
          height: 25px;
          display: block;
          border: 1px solid $theme-dark-border-color;
          color: $theme-black-color;
          cursor: pointer;
          border-radius: 0px;
          border-left: none;
          @include breakpoint(small down) {
            width: 20px;
            height: 20px;
            line-height: 20px;
          }
        }
        .icon-arrow-1-up{
          border-bottom: 0px;
        }
        &:hover{
          background: $theme-orange-color;
          i{
            color: $theme-white-color;
          }
        }
      }
    }
  }
  .cart-item-info{
    strong.cart-item-value{
      font-weight: 600;
    }
    .cart-item-value {
      line-height: 24px;
      font-weight: 400;
    }
    .cart-remove{
      background: none;
      width: 20px;
      height: 20px;
      margin: 0px 0 0 20px;
      @include breakpoint(small down){
        margin: 0 0 0 7px;
      }
      i{
        color: $theme-orange-color;
      }
      &:hover{
        i{
          color:$theme-black-color;
        }
      }
    }
  }
}
.cart-totals{
  .cart-total{
    padding: 0 10px;
    .cart-total-label, .cart-total-value{
      padding: 13px 0;
      strong{
        font-weight: 600;
        font-size: 14px;
        color: $theme-black-color;
      }
    }
    .cart-total-value{
      font-weight: 400;
    }
    .cart-total-grandTotal{
      font-weight: 600;
      font-size: 20px;
    }
  }
}
.cart-additionalCheckoutButtons{
  .CheckoutButton{
    margin: 5px 0;
    &:last-child{
      margin-top: 10px;
    }
    &:first-child{
      margin-bottom: 10px;
    }
    .or-use-label{
      margin-top: 0px;
      margin-bottom: 8px;
      font-weight: 400;
    }
  }
}
.giftWrapping-form{
  .form-actions{
    margin-bottom: 20px;
  }
}
/* ==:: CART PAGE STYLE END ::==*/

// LOGIN PAGE STYLE -------------------------------------------------------------
/* ==:: LOGIN PAGE STYLE START ::==*/
.login-box-top{
  @include breakpoint(mob-large down){
    margin-bottom: 30px;
  }
}
.login-box-inner{
  padding: 30px;
  .form-field {
    position: relative;
    .icon-user, .icon-lock{
      position: absolute;
      left: 10px;
      top: 46px;
    }
  }
  .form-actions{
    text-align: left;
  }
  .button, .forgot-password {
    display: inline-block;
    vertical-align: middle;
  }
  .forgot-password{
    @include breakpoint(small down){
      font-size: 12px;
    }
  }
  .panel-body{
    background: none;
    margin: 0px;
    padding: 0px;
    ul{
      @include breakpoint(small down){
        margin-left: 0px;
      }
    }
    p.new-customer-intro{
      margin-bottom: 17px;
    }
    a{
      .button{
        margin: 0px;
      }
    }
  }
}
/* ==:: LOGIN PAGE STYLE END ::==*/
