// ACCOUNT PAGE STYLE -------------------------------------------------------------
/* ==:: ACCOUNT PAGE STYLE START ::==*/
.navBar--sub{
  .navBar-item{
    position: relative;
      @include breakpoint(medium down){
        margin: 0 6px 15px;
      }
      @include breakpoint(mob-large down){
        margin: 0 5px 15px;
        width: 48%;
        float: left;
        &:nth-child(odd){
          clear: both;
        }
      }
      @include breakpoint(mob-small down){
        width: 47%;
      }
      @include breakpoint(small down){
        width: 100%;
        margin: 0 0 15px 0;
      }
      a{
        padding: 10px 15px;
        border: 1px solid $theme-border-color;
        &:hover{
          color: $theme-orange-color;
          border: 1px solid $theme-orange-color;
        }
      }
      &:after{
        border: 10px solid transparent;
        position: absolute;
        bottom: -20px;
        left: 50%;
        transform: translateX(-50%);
        -webkit-transform: translateX(-50%);
        content: '';
      }
  }
  .navBar-item.navBar-item--separate{
    border: 0px;
  }
  .navBar-item.is-active{
    padding: 10px 15px;
    border: none;
    color: $theme-white-color;
    background: $theme-orange-color;
    &:after{
      border-top-color: $theme-orange-color;
    }
  }
}
/* ==:: ACCOUNT PAGE STYLE END ::==*/
