_customvariable.scss:
$stencil-primary-color:   stencilColor("stencil-primary-color");
$theme-orange-color: $stencil-primary-color;

$pencil-banner-bg: stencilColor("pencil-banner-bg");

/* ==================== THEME VARIABLE ==================== */

$theme-base-font-size: 14px;
$theme-base-font-family:  stencilFontFamily("body-font");
$theme-base-title-font-family:  stencilFontFamily("headings-font");
$Hero-Carousel-Font: stencilFontFamily("Hero-Carousel-Font");

$font-weight-300: 300;
$font-weight-400: 400;
$font-weight-500: 500;
$font-weight-600: 600;
$font-weight-700: 700;
$font-weight-800: 800;

@if $theme-base-font-family == 'Roboto' {
    $font-weight-500: 500;
  } @else if $theme-base-font-family == "Muli" {
    $font-weight-500: 500;
  } @else if $theme-base-font-family == "Noto Sans" {
    $font-weight-500: 500;
  } @else if $theme-base-font-family == "Work Sans" {
    $font-weight-500: 500;
  } @else {
    $font-weight-500: 500;
  }

  @if $theme-base-title-font-family == 'Roboto' {
      $font-weight-500: 500;
    } @else if $theme-base-font-family == "Muli" {
      $font-weight-500: 700;
    } @else if $theme-base-font-family == "Noto Sans" {
      $font-weight-500: 700;
    } @else if $theme-base-font-family == "Work Sans" {
      $font-weight-500: 600;
    } @else if $theme-base-font-family == "Roboto Slab"{
      $font-weight-500: 700;
    }


$theme-body-font-color: #222222;
