components/category/sub-category-block:
{{#if theme_settings.SubCategoryStyle '==' 'GridView'}}
{{#if category}}
  {{#if category.subcategories}}
    <div class="sub-category-wrap clearfix">
        <ul class="sub-category-block custom-row clearfix align-center">
          {{#each category.subcategories}}
          <li class="column sub-category-item clearfix">
              <div class="category-image-inner">
                <a class="sub-category-img-block" href="{{url}}" alt="{{name}}" title="{{name}}">
                  <div class="sub-category-img">
                  {{#if image}}
                    <img class="lazyload" data-sizes="auto" src="{{cdn 'img/loading.svg'}}" data-src="{{getImage image 'sub-category-img'}}" alt="" />
                  {{else}}
                    <img class="sub-no-img" src="{{cdn 'assets/img/noImage.jpg'}}" alt="No Image" title="No Image">
                  {{/if}}
                  </div>
                </a>
                <p class="sub-category-name">
                  <a href="{{url}}">{{name}}</a>
                </p>
              </div>
          </li>
          {{/each}}
        </ul>
    </div>
    {{/if}}
  {{/if}}
{{/if}}


{{#if theme_settings.SubCategoryStyle '==' 'ListView'}}
  {{#if category}}
    {{#if category.subcategories}}
      <div class="sub-category-wrap clearfix">
          <div class="custom-row">
            <ul class="sub-category-list-view-block clearfix list-with-arrow">
              {{#each category.subcategories}}
              <li class="column xlarge-3 large-4 mob-large-6 mob-mid-6 mob-small-6 small-12">
                <a href="{{url}}" alt="{{name}}" title="{{name}}">
                  <span>{{name}}</span>
                </a>
              </li>
              {{/each}}
            </ul>
          </div>
      </div>
      {{/if}}
    {{/if}}
  {{/if}}
