components/products/description-tabs:
{{#or product.description product.warranty product.videos.list.length product.reviews.total}}
<div id="Product-descrTabs">
  <ul class="tabs clearfix">
    {{#if product.description}}
    <li class="tab is-active">
      <a class="tab-title" href="#tab-description">{{lang 'products.description'}}</a>
    </li>
    {{/if}}
    {{#if product.warranty}}
    <li class="tab">
      <a class="tab-title" href="#tab-warranty">{{lang 'products.warranty'}}</a>
    </li>
    {{/if}}
    {{#if product.videos.list.length}}
    <li class="tab">
      <a class="tab-title" href="#tab-video">{{lang 'products.videos.header'}}</a>
    </li>
    {{/if}}
    {{#if settings.show_product_reviews}}
    {{#if product.reviews.total}}
    <li class="tab">
      <a class="tab-title" href="#tab-reviews">{{lang 'products.reviews.r_title'}}</a>
    </li>
    {{/if}}
    {{/if}}
  </ul>
  {{#if product.description}}
  <div class="tab-content" id="tab-description">
    {{{product.description}}}
    {{{snippet 'product_description'}}}
  </div>
  {{/if}}
  {{#if product.warranty}}
  <div class="tab-content" id="tab-warranty">
    {{{product.warranty}}}
  </div>
  {{/if}}
  {{#if product.videos.list.length}}
  <div class="tab-content" id="tab-video">
    {{> components/products/videos product.videos}}
  </div>
  {{/if}}
  {{#if settings.show_product_reviews}}
  {{#if product.reviews.total}}
  <div class="tab-content" id="tab-reviews">
    {{> components/products/reviews reviews=product.reviews product=product urls=urls}}
  </div>
  {{/if}}
  {{/if}}
</div>
{{/or}}

components/products/reviews:
{{#if reviews.total}}
<section class="toggle productReviews" id="product-reviews" data-product-reviews>
    <div class="toggle-content is-open" id="productReviews-content" aria-hidden="false">
        <ul class="productReviews-list" id="productReviews-list">
            {{#each reviews.list}}
            <li class="productReview">
                <article itemprop="review" itemscope itemtype="http://schema.org/Review">
                    <header>
                        <span class="productReview-rating rating--small" itemprop="reviewRating" itemscope itemtype="http://schema.org/Rating">
                            {{> components/products/ratings rating=rating}}
                            <span class="productReview-ratingNumber" itemprop="ratingValue">{{ rating }}</span>
                        </span>
                        <h5 itemprop="name" class="productReview-title">{{ title }}</h5>
                        {{#if name}}
                            <meta itemprop="author" content="{{name}}">
                            <p class="productReview-author">
                                {{lang 'products.reviews.post_on_by' date=date name=name }}
                            </p>
                        {{else}}
                            <p class="productReview-author">
                                {{lang 'products.reviews.post_on_by' date=date name=(lang 'products.reviews.anonymous_poster')}}
                            </p>
                        {{/if}}
                    </header>
                    <p itemprop="reviewBody" class="productReview-body">{{nl2br text}}</p>
                </article>
            </li>
            {{/each}}
        </ul>
        {{> components/common/paginator pagination.reviews}}
    </div>
</section>
{{/if}}
{{{snippet 'reviews'}}}
