pages/errors/403:
{{#partial "page"}}

{{> components/common/breadcrumbs breadcrumbs=breadcrumbs}}
<h1 class="page-heading">{{lang 'forbidden.page_heading'}}</h1>
<main class="page">
    <section class="page-content page-content--centered">
        <p class="u-textAlignCenter">
            {{lang 'forbidden.if_you_were_signed_in'}}
            <a href="{{urls.auth.login}}">{{lang 'forbidden.sign_in'}}</a>
            {{lang 'forbidden.to_continue'}}
        </p>
        {{> components/common/search-box}}
    </section>
</main>

{{/partial}}
{{> layout/base}}

components/common/pencil-banner:
{{#if theme_settings.EnablePencilBanner}}
  {{#or theme_settings.Pencilbannercontain theme_settings.Pencilbannercontain }}
    <div class="pencil-banner">
      <div class="container">
        {{#if theme_settings.Pencilbannercontain}}
        <span class="banner-contain">{{theme_settings.Pencilbannercontain}} &nbsp;
          {{#and theme_settings.Pencilbannershopbutton theme_settings.PencilbannershopUrl}}
          <a href="{{theme_settings.PencilbannershopUrl}}">{{theme_settings.Pencilbannershopbutton}}</a>
          {{/and}}
        {{/if}}
        </span>
        <a class="control-close-btn blue-close" href="javascript:void(0);"><i class="icon-close"></i></a>
      </div>
      <a class="control-close-btn default-close" href="javascript:void(0);"><i class="icon-close"></i></a>
  </div>
  {{/or}}
{{/if}}

components/common/service-section:
{{#if theme_settings.show_usp '>=' 0}}
<div class="container">
  <div class="service-wrap m-b-30 m-t-30">
        <div class="custom-row">
          {{#if theme_settings.show_usp '>=' 1}}

          <div class="column large-3 medium-3 mob-large-6 mob-mid-6 mob-small-6 small-6 one">
            <div class="service-banner-item">

            {{#if theme_settings.ServiceImageFirst}}
              <div class="service-banner-img">
                <div class="service-banner-img-inn">
                  <img class="lazyload" data-sizes="auto" src="{{cdn 'img/loading.svg'}}" data-src="/product_images/uploaded_images/{{theme_settings.ServiceImageFirst}}" alt="" />
                </div>
              </div>
            {{/if}}

              <div class="service-banner-detail">
                {{#if theme_settings.ServiceTitleFirst}}
                  <h4>{{theme_settings.ServiceTitleFirst}}</h4>
                {{/if}}

                {{#if theme_settings.ServiceTextFirst}}
                  <span>{{theme_settings.ServiceTextFirst}}</span>
                {{/if}}
              </div>
            </div>
          </div>
          {{/if}}

        {{#if theme_settings.show_usp '>=' 2}}
          <div class="column large-3 medium-3 mob-large-6 mob-mid-6 mob-small-6 small-6 two">
            <div class="service-banner-item">

              {{#if theme_settings.ServiceImageSecond}}
                <div class="service-banner-img">
                  <div class="service-banner-img-inn">
                    <img class="lazyload" data-sizes="auto" src="{{cdn 'img/loading.svg'}}" data-src="/product_images/uploaded_images/{{theme_settings.ServiceImageSecond}}" alt="" />
                  </div>
                </div>
              {{/if}}

              <div class="service-banner-detail">

                {{#if theme_settings.ServiceTitleSecond}}
                  <h4>{{theme_settings.ServiceTitleSecond}}</h4>
                {{/if}}

                {{#if theme_settings.ServiceTextSecond}}
                  <span>{{theme_settings.ServiceTextSecond}}</span>
                {{/if}}

              </div>
            </div>
          </div>
          {{/if}}

          {{#if theme_settings.show_usp '>=' 3}}

          <div class="column large-3 medium-3 mob-large-6 mob-mid-6 mob-small-6 small-6 three">
            <div class="service-banner-item">

              {{#if theme_settings.ServiceImageThird}}
                <div class="service-banner-img">
                  <div class="service-banner-img-inn">
                      <img class="lazyload" data-sizes="auto" src="{{cdn 'img/loading.svg'}}" data-src="/product_images/uploaded_images/{{theme_settings.ServiceImageThird}}" alt="" />
                  </div>
                </div>
              {{/if}}

              <div class="service-banner-detail">
                {{#if theme_settings.ServiceTitleThird}}
                  <h4>{{theme_settings.ServiceTitleThird}}</h4>
                {{/if}}

                {{#if theme_settings.ServiceTextThird}}
                  <span>{{theme_settings.ServiceTextThird}}</span>
                {{/if}}
              </div>
            </div>
          </div>

        {{/if}}

        {{#if theme_settings.show_usp '>=' 4}}

          <div class="column large-3 medium-3 mob-large-6 mob-mid-6 mob-small-6 small-6 four">
            <div class="service-banner-item">

              {{#if theme_settings.ServiceImageFour}}
                <div class="service-banner-img">
                  <div class="service-banner-img-inn">
                      <img class="lazyload" data-sizes="auto" src="{{cdn 'img/loading.svg'}}" data-src="/product_images/uploaded_images/{{theme_settings.ServiceImageFour}}" alt="" />
                  </div>
                </div>
              {{/if}}

              <div class="service-banner-detail">

                {{#if theme_settings.ServiceTitleFour}}
                  <h4>{{theme_settings.ServiceTitleFour}}</h4>
                {{/if}}

                {{#if theme_settings.ServiceTextFour}}
                  <span>{{theme_settings.ServiceTextFour}}</span>
                {{/if}}

              </div>
            </div>
          </div>
          {{/if}}

        </div>

  </div>
</div>
{{/if}}

components/common/social-links:
{{#if social_media}}
    <ul class="socialLinks socialLinks--alt">
        {{#each social_media}}
            <li class="socialLinks-item">
              {{#if display_name '===' 'google+'}}
                  <a href="{{url}}" target="_blank">
                      <span class="google-plus"></span>
                  </a>
              {{else}}
                  <a href="{{url}}" target="_blank">
                      <span class="{{display_name}}"></span>
                  </a>
              {{/if}}
            </li>
        {{/each}}
    </ul>
{{/if}}

components/common/payment-icons:
{{#with theme_settings}}
    {{#or show_accept_amex show_accept_discover show_accept_mastercard show_accept_paypal show_accept_visa}}
