components/carousel:
  {{#if carousel.slides}}
<div class="heroCarousel-wrap">

  <a class="heroCarousel-down-arrow one-page-scroll" title="Click to Down">
      <i class="icon-arrow-1-down"></i>
  </a>
  <div class="slider-loading-icon">
  <div class="sk-cube-grid">
  <div class="sk-cube sk-cube1"></div>
  <div class="sk-cube sk-cube2"></div>
  <div class="sk-cube sk-cube3"></div>
  <div class="sk-cube sk-cube4"></div>
  <div class="sk-cube sk-cube5"></div>
  <div class="sk-cube sk-cube6"></div>
  <div class="sk-cube sk-cube7"></div>
  <div class="sk-cube sk-cube8"></div>
  <div class="sk-cube sk-cube9"></div>
  </div>
  </div>

  <section class="heroCarousel"
      data-slick='{
          "dots": {{#if (length carousel.slides) ">" "1"}}true{{else}}false{{/if}},
          "mobileFirst": true,
          "slidesToShow": 1,
          "slidesToScroll": 1,
          "adaptiveHeight": true,
          "autoplay": true,
          "autoplaySpeed": {{carousel.swap_frequency}},
          "lazyLoad": "anticipated"
      }'>
      {{#each carousel.slides}}
      {{#if url}}
      <a href="{{url}}">
          <div class="heroCarousel-slide SlideItem-{{@index}} {{#if ../theme_settings.homepage_stretch_carousel_images}}stretch{{/if}} {{#if @first}}heroCarousel-slide--first{{/if}}">
              <div class="heroCarousel-image-wrapper" {{#if image_height}}{{#if image_width}}style="height: {{multiply (divide image_height image_width) 100}}vw"{{/if}}{{/if}}>
                  <img class="heroCarousel-image" data-lazy="{{image}}" alt="{{alt_text}}" title="{{alt_text}}"
                  {{#if image_width}}width="{{image_width}}"{{/if}} {{#if image_height}}height="{{image_height}}"{{/if}} />
              </div>
              {{#if heading}}
                  {{> components/carousel-content show_background=true}}
              {{else}}
                  {{#if text}}
                      {{> components/carousel-content show_background=true}}
                  {{else}}
                      {{#if button_text}}
                          {{> components/carousel-content show_background=false}}
                      {{/if}}
                  {{/if}}
              {{/if}}
          </div>
      </a>
      {{else}}
      <div class="heroCarousel-slide SlideItem-{{@index}} {{#if ../theme_settings.homepage_stretch_carousel_images}}stretch{{/if}} {{#if @first}}heroCarousel-slide--first{{/if}}">
          <div class="heroCarousel-image-wrapper" {{#if image_height}}{{#if image_width}}style="height: {{multiply (divide image_height image_width) 100}}vw"{{/if}}{{/if}}>
              <img class="heroCarousel-image" data-lazy="{{image}}" alt="{{alt_text}}" title="{{alt_text}}"
              {{#if image_width}}width="{{image_width}}"{{/if}} {{#if image_height}}height="{{image_height}}"{{/if}} />
          </div>
          {{#if heading}}
              {{> components/carousel-content show_background=true}}
          {{else}}
              {{#if text}}
                  {{> components/carousel-content show_background=true}}
              {{else}}
                  {{#if button_text}}
                      {{> components/carousel-content show_background=false}}
                  {{/if}}
              {{/if}}
          {{/if}}
      </div>
      {{/if}}
      {{/each}}
  </section>
</div>
{{/if}}
