components/products/price:
{{#and price.price_range (if theme_settings.price_ranges '==' true)}}
    {{> components/products/price-range price=price schema_org=schema_org}}
{{else}}
    {{#if price.with_tax}}
        <div class="price-section price-section--withTax" {{#if schema_org}}itemprop="offers" itemscope itemtype="http://schema.org/Offer"{{/if}}>
            <span class="price-label" {{#if price.non_sale_price_with_tax}}style="display: none;"{{/if}}>
                {{theme_settings.pdp-price-label}}
            </span>
            <span class="price-now-label" {{#unless price.non_sale_price_with_tax}}style="display: none;"{{/unless}}>
                {{theme_settings.pdp-sale-price-label}}
            </span>
            <span data-product-price-with-tax class="price"> {{price.with_tax.formatted}}</span>
            {{#if schema_org}}
                <meta itemprop="availability" content="{{product.availability}}">
                <meta itemprop="availability" itemtype="http://schema.org/ItemAvailability"
                    content="http://schema.org/{{#if product.pre_order}}PreOrder{{else if product.out_of_stock}}OutOfStock{{else if product.can_purchase '===' false}}OutOfStock{{else}}InStock{{/if}}">
                <div itemprop="priceSpecification" itemscope itemtype="http://schema.org/PriceSpecification">
                    <meta itemprop="price" content="{{price.with_tax.value}}">
                    <meta itemprop="priceCurrency" content="{{currency_selector.active_currency_code}}">
                    <meta itemprop="valueAddedTaxIncluded" content="true">
                </div>
            {{/if}}
            {{#if price.without_tax}}
                <abbr title="{{lang 'products.excluding_tax'}}">{{lang 'products.price_with_tax' tax_label=price.tax_label}}</abbr>
            {{/if}}
        </div>
        {{#if price.rrp_with_tax}}
        <div class="price-section price-section--withTax rrp-price--withTax" {{#unless price.rrp_with_tax}}style="display: none;"{{/unless}}>
            <span class="price-now-label rrp-price--withTax-cus" >{{theme_settings.pdp-retail-price-label}}</span>
            <span data-product-rrp-with-tax class="price price--rrp">
                {{price.rrp_with_tax.formatted}}
            </span>
          </div>
        {{/if}}
        {{#if price.non_sale_price_with_tax}}
        <div class="price-section price-section--withTax non-sale-price--withTax" {{#unless price.non_sale_price_with_tax}}style="display: none;"{{/unless}}>
            <span class="price-now-label non-sale-price--withTax-cus" >{{theme_settings.pdp-non-sale-price-label}}</span>
            <span data-product-non-sale-price-with-tax class="price price--non-sale">
                {{price.non_sale_price_with_tax.formatted}}
            </span>
        </div>
        {{/if}}
    {{/if}}
    {{#if price.without_tax}}
        <div class="price-section price-section--withoutTax" {{#if schema_org}}itemprop="offers" itemscope itemtype="http://schema.org/Offer"{{/if}}>
            <span class="price-label" {{#if price.non_sale_price_without_tax}}style="display: none;"{{/if}}>
                {{theme_settings.pdp-price-label}}
            </span>
            <span class="price-now-label" {{#unless price.non_sale_price_without_tax}}style="display: none;"{{/unless}}>
                {{theme_settings.pdp-sale-price-label}}
        </span>
        <span data-product-price-without-tax class="price"> {{price.without_tax.formatted}}</span>
        {{#if schema_org}}
            <meta itemprop="availability" itemtype="http://schema.org/ItemAvailability"
                content="http://schema.org/{{#if product.pre_order}}PreOrder{{else if product.out_of_stock}}OutOfStock{{else if product.can_purchase '===' false}}OutOfStock{{else}}InStock{{/if}}">
            <meta itemprop="itemCondition" itemtype="http://schema.org/OfferItemCondition" content="http://schema.org/{{product.condition}}Condition">
            <div itemprop="priceSpecification" itemscope itemtype="http://schema.org/PriceSpecification">
                <meta itemprop="price" content="{{price.without_tax.value}}">
                <meta itemprop="priceCurrency" content="{{currency_selector.active_currency_code}}">
                <meta itemprop="valueAddedTaxIncluded" content="false">
            </div>
        {{/if}}
        {{#if price.with_tax}}
            <abbr title="{{lang 'products.including_tax'}}">{{lang 'products.price_with_tax' tax_label=price.tax_label}}</abbr>
        {{/if}}
    </div>
    {{#if price.rrp_without_tax}}
        <div class="price-section price-section--withoutTax rrp-price--withoutTax {{#if price.with_tax}}price-section--minor{{/if}}" {{#unless price.rrp_without_tax}}style="display: none;"{{/unless}}>
            <span class="price-now-label rrp-price--withoutTax-cus" >{{theme_settings.pdp-retail-price-label}}</span>
            <span data-product-rrp-price-without-tax class="price price--rrp">
                {{price.rrp_without_tax.formatted}}
            </span>
        </div>
    {{/if}}
    {{#if price.non_sale_price_without_tax}}
        <div class="price-section price-section--withoutTax non-sale-price--withoutTax {{#if price.with_tax}}price-section--minor{{/if}}" {{#unless price.non_sale_price_without_tax}}style="display: none;"{{/unless}}>
            <span class="price-now-label non-sale-price--withoutTax-cus" >{{theme_settings.pdp-non-sale-price-label}}</span>
            <span data-product-non-sale-price-without-tax class="price price--non-sale">
                {{price.non_sale_price_without_tax.formatted}}
            </span>
        </div>
    {{/if}}

    {{/if}}
    {{#if page_type '===' 'product'}}
        {{#if price.saved}}
             <div class="price-section price-section--saving price">
                    <span class="price">{{lang 'products.you_save_opening_text'}}</span>
                    <span data-product-price-saved class="price">
                        {{price.saved.formatted}}
                    </span>
                    <span class="price">{{lang 'products.you_save_closing_bracket'}}</span>
             </div>
        {{/if}}
    {{/if}}

{{/and}}

components/products/bulk-discount-rates:
{{#if bulk_discount_rates.length}}
    {{#if product}}<dt class="productView-info-name">{{lang 'products.bulk_pricing.title'}}</dt>{{/if}}
    <dd class="productView-info-value">
      <a href="{{url}}"
        {{#unless is_ajax }}data-reveal-id="bulkPricingModal{{#if id}}-{{id}}-{{$index}}{{/if}}"{{/unless}}>
            {{lang 'products.bulk_pricing.view'}}
        </a>
    </dd>
    <div id="bulkPricingModal{{#if id}}-{{id}}-{{$index}}{{/if}}" class="modal" data-reveal>
        <div class="modal-header">
            <h2 class="modal-header-title">{{lang 'products.bulk_pricing.modal_title'}}</h2>
            <a href="#" class="modal-close" aria-label="Close"><span aria-hidden="true">&#215;</span></a>
        </div>
        <div class="modal-body">
            <p>{{lang 'products.bulk_pricing.instructions'}}</p>
            <ul>
              {{#each bulk_discount_rates}}
                  <li>
                      {{lang 'products.bulk_pricing.range' min=min max=max}}
                      {{#if type '===' 'percent'}}
                      {{lang 'products.bulk_pricing.percent' discount=discount.formatted}}
                      {{/if}}
                      {{#if type '===' 'fixed'}}
                      {{lang 'products.bulk_pricing.fixed' discount=discount.formatted}}
                      {{/if}}
                      {{#if type '===' 'price'}}
                      {{lang 'products.bulk_pricing.price' discount=discount.formatted}}
                      {{/if}}
                  </li>
              {{/each}}
            </ul>
        </div>
    </div>
{{/if}}

components/common/share:
{{#if settings.add_this.buttons}}
    <div class="addthis_toolbox" addthis:url="{{url}}"
         addthis:title="{{title}}">
        <ul class="socialLinks-alt">
            {{#each settings.add_this.buttons}}
                <li class="socialLinks-item socialLinks-item-alt socialLinks-item--{{service}}">
                    <a class="addthis_button_{{service}} icon icon--{{service}}" {{{annotation}}}>
                        {{#if service '===' 'facebook'}}
                          <span class="facebook"></span>
                        {{/if}}

                        {{#if service '===' 'twitter'}}
                            <span class="twitter"></span>
                        {{/if}}

                        {{#if service '===' 'pinterest'}}
                          <span class="pinterest"></span>
                        {{/if}}

                        {{#if service '===' 'linkedin'}}
                            <span class="linkedin"></span>
                        {{/if}}

                        {{#if service '===' 'google_plusone'}}
                            <!-- No custom icons allowed with g+ -->
                            <span class="google-plus"></span>
                        {{/if}}

                        {{#if service '===' 'google'}}
                            <span class="google"></span>
                        {{/if}}

                        {{#if service '===' 'email'}}
                          <span class="envelope"></span>
                        {{/if}}

                        {{#if service '===' 'print'}}
                          <span class="print"></span>
                        {{/if}}


                    </a>
                </li>
            {{/each}}

            {{#inArray (arrayify (pluck (limit settings.add_this.buttons 20) 'service')) 'facebook_like'}}
                {{#each settings.add_this.buttons}}
                    {{#if service '==' 'facebook_like'}}
                    <li class="{{service}}" style="display: inline-block;width: 100%;height: 100px; ">
                        <div class="addthis_button_{{service}} icon icon--{{service}}" style="width: 100%;
                        height: 100%; text-align:center;" {{{annotation}}}>
                              <iframe src="https://www.facebook.com/plugins/like.php?href={{../../postURL}}&width=450&layout=standard&action=like&size=small&show_faces=true&share=false&height=80&appId" width="400" height="20" style="border:none;overflow:hidden;" scrolling="no" frameborder="0" allowTransparency="true" allow="encrypted-media"></iframe>
                        </div>
                    </li>
                    {{/if}}
                {{/each}}
            {{/inArray}}

        </ul>

        <script type="text/javascript"
                src="//s7.addthis.com/js/300/addthis_widget.js#pubid=ra-4e94ed470ee51e32"></script>
        <script>
            if (typeof(addthis) === "object") {
                addthis.toolbox('.addthis_toolbox');
            }
        </script>
    </div>
{{/if}}

